import React, { useState } from 'react';
import { motion } from 'framer-motion';

function App() {
  const [currentStep, setCurrentStep] = useState(1);
  const totalSteps = 7;

  // Form data state
  const [formData, setFormData] = useState({
    // Personal Information
    fullName: '',
    dateOfBirth: '',
    gender: '',
    identificationNumber: '',

    // Contact Information
    phoneNumber: '',
    email: '',
    address: '',

    // Emergency Contact
    emergencyName: '',
    emergencyPhone: '',
    emergencyRelation: '',

    // Program
    preferredProgram: '',
    joiningDate: '',

    // Accommodation
    requiresAccommodation: false,
    checkInDate: '',

    // Airport Pickup
    requiresPickup: false,
    arrivalDate: '',
    arrivalTime: '',

    // Additional
    photo: null
  });

  // Add validation state
  const [errors, setErrors] = useState({});

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  // Validation function
  const validateStep = (step) => {
    const newErrors = {};

    switch(step) {
      case 1:
        if (!formData.fullName) newErrors.fullName = 'Full name is required';
        if (!formData.dateOfBirth) newErrors.dateOfBirth = 'Date of birth is required';
        if (!formData.gender) newErrors.gender = 'Gender is required';
        if (!formData.identificationNumber) newErrors.identificationNumber = 'ID number is required';
        break;
      case 2:
        if (!formData.phoneNumber) newErrors.phoneNumber = 'Phone number is required';
        if (!formData.email) newErrors.email = 'Email is required';
        if (!formData.address) newErrors.address = 'Address is required';
        break;
      // Add validation for other steps
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const renderStepContent = () => {
    switch(currentStep) {
      case 1:
        return (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-2">
              <label className="block text-[#1B3168] text-sm font-medium">
                Full Name
              </label>
              <input 
                type="text"
                name="fullName"
                value={formData.fullName}
                onChange={handleInputChange}
                placeholder="Enter your full name"
                className={inputClassName}
              />
            </div>
            <div className="space-y-2">
              <label className="block text-[#1B3168] text-sm font-medium">
                Date of Birth
              </label>
              <input 
                type="date"
                name="dateOfBirth"
                value={formData.dateOfBirth}
                onChange={handleInputChange}
                className={inputClassName}
              />
            </div>
            <div className="space-y-2">
              <label className="block text-[#1B3168] text-sm font-medium">
                Gender
              </label>
              <select 
                name="gender"
                value={formData.gender}
                onChange={handleInputChange}
                className={inputClassName}
              >
                <option value="">Select Gender</option>
                <option value="male">Male</option>
                <option value="female">Female</option>
                <option value="other">Other</option>
              </select>
            </div>
            <div className="space-y-2">
              <label className="block text-[#1B3168] text-sm font-medium">
                ID/Passport Number
              </label>
              <input 
                type="text"
                name="identificationNumber"
                value={formData.identificationNumber}
                onChange={handleInputChange}
                placeholder="Enter ID/Passport number"
                className={inputClassName}
              />
            </div>
          </div>
        );

      case 2:
        return (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-2">
              <label className="block text-[#1B3168] text-sm font-medium">
                Phone Number
              </label>
              <input 
                type="tel"
                name="phoneNumber"
                value={formData.phoneNumber}
                onChange={handleInputChange}
                placeholder="Enter phone number"
                className={inputClassName}
              />
            </div>
            <div className="space-y-2">
              <label className="block text-[#1B3168] text-sm font-medium">
                Email Address
              </label>
              <input 
                type="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                placeholder="Enter email address"
                className={inputClassName}
              />
            </div>
            <div className="md:col-span-2 space-y-2">
              <label className="block text-[#1B3168] text-sm font-medium">
                Address
              </label>
              <textarea 
                name="address"
                value={formData.address}
                onChange={handleInputChange}
                placeholder="Enter your address"
                className={`${inputClassName} resize-none`}
                rows={3}
              />
            </div>
          </div>
        );

      case 3:
        return (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-2">
              <label className="block text-[#1B3168] text-sm font-medium">
                Emergency Contact Name
              </label>
              <input 
                type="text"
                name="emergencyName"
                value={formData.emergencyName}
                onChange={handleInputChange}
                placeholder="Emergency contact name"
                className={inputClassName}
              />
            </div>
            <div className="space-y-2">
              <label className="block text-[#1B3168] text-sm font-medium">
                Emergency Contact Phone
              </label>
              <input 
                type="tel"
                name="emergencyPhone"
                value={formData.emergencyPhone}
                onChange={handleInputChange}
                placeholder="Emergency contact phone"
                className={inputClassName}
              />
            </div>
            <div className="space-y-2">
              <label className="block text-[#1B3168] text-sm font-medium">
                Relationship
              </label>
              <input 
                type="text"
                name="emergencyRelation"
                value={formData.emergencyRelation}
                onChange={handleInputChange}
                placeholder="Relationship to emergency contact"
                className={inputClassName}
              />
            </div>
          </div>
        );

      // Add more cases for remaining steps...
      default:
        return null;
    }
  };

  const getStepTitle = () => {
    switch(currentStep) {
      case 1: return "Personal Information";
      case 2: return "Contact Information";
      case 3: return "Emergency Contact";
      case 4: return "Program Enrollment";
      case 5: return "Accommodation";
      case 6: return "Airport Pickup";
      case 7: return "Additional Information";
      default: return "";
    }
  };

  const handleContinue = () => {
    if (validateStep(currentStep)) {
      if (currentStep < totalSteps) {
        setCurrentStep(currentStep + 1);
      } else {
        // Handle form submission
        console.log('Form submitted:', formData);
      }
    }
  };

  // Updated program enrollment step
  const renderProgramStep = () => (
    <div className="space-y-6">
      <div className="grid grid-cols-1 gap-6">
        <div className="space-y-4">
          <label className="block text-[#1B3168] text-lg font-medium mb-4">
            Select Your Program
          </label>
          {[
            { id: '75hrs', name: '75HRS Course', description: 'Comprehensive training program for caregivers' },
            { id: 'firstaid', name: 'FIRST AID & CPR', description: 'Essential life-saving techniques and certification' },
            { id: 'hca', name: 'HCA SKILLS EXAM PRACTICE', description: 'Preparation for HCA certification exam' }
          ].map((program) => (
            <motion.div
              key={program.id}
              whileHover={{ scale: 1.02 }}
              className={`p-4 rounded-2xl border-2 cursor-pointer transition-all
                ${formData.preferredProgram === program.id 
                  ? 'border-[#4CAF50] bg-[#4CAF50]/5' 
                  : 'border-gray-200 hover:border-gray-300 bg-white'}`}
              onClick={() => handleInputChange({ 
                target: { name: 'preferredProgram', value: program.id }
              })}
            >
              <div className="flex items-center space-x-4">
                <div className={`w-5 h-5 rounded-full border-2 flex items-center justify-center
                  ${formData.preferredProgram === program.id 
                    ? 'border-[#4CAF50] bg-[#4CAF50]' 
                    : 'border-gray-300'}`}
                >
                  {formData.preferredProgram === program.id && (
                    <motion.div
                      initial={{ scale: 0 }}
                      animate={{ scale: 1 }}
                      className="w-2 h-2 bg-white rounded-full"
                    />
                  )}
                </div>
                <div>
                  <h3 className="font-semibold text-[#1B3168]">{program.name}</h3>
                  <p className="text-gray-600 text-sm">{program.description}</p>
                </div>
              </div>
            </motion.div>
          ))}
        </div>

        <div className="space-y-2">
          <label className="block text-[#1B3168] text-sm font-medium">
            Preferred Start Date
          </label>
          <input 
            type="date"
            name="joiningDate"
            value={formData.joiningDate}
            onChange={handleInputChange}
            className={inputClassName}
            min={new Date().toISOString().split('T')[0]}
          />
          {errors.joiningDate && (
            <p className="text-red-500 text-sm mt-1">{errors.joiningDate}</p>
          )}
        </div>
      </div>
    </div>
  );

  // Updated input styles
  const inputClassName = `
    w-full px-6 py-4 
    bg-white 
    border-2 border-gray-200 
    rounded-2xl
    text-[#1B3168] 
    placeholder-[#1B3168]/40
    focus:outline-none 
    focus:border-[#4CAF50] 
    focus:ring-2 
    focus:ring-[#4CAF50]/20
    transition-all 
    duration-300
    shadow-sm
    hover:border-gray-300
  `;

  return (
    <div className="min-h-screen w-full flex items-center justify-center bg-gradient-to-br from-slate-800 via-[#1B3168] to-slate-900 p-4 relative overflow-hidden">
      {/* Professional background pattern overlay */}
      <div className="absolute inset-0 bg-grid-white/[0.05] bg-[size:20px_20px]" />

      {/* Subtle gradient orbs for depth */}
      <div className="absolute top-0 left-0 w-96 h-96 bg-blue-500/10 rounded-full filter blur-3xl" />
      <div className="absolute bottom-0 right-0 w-96 h-96 bg-indigo-500/10 rounded-full filter blur-3xl" />

      {/* Logo Section */}
      <div className="absolute top-4 left-4 md:left-8 z-10">
        <img src="/logo.png" alt="Huduma Center Logo" className="h-16 md:h-20" />
      </div>

      <div className="relative w-full max-w-4xl mx-auto mt-20 z-10">
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="rounded-3xl shadow-2xl overflow-hidden"
        >
          {/* Header Section */}
          <div className="relative p-8 bg-gradient-to-r from-[#1B3168] to-[#2A4580]">
            <div className="relative z-10">
              <motion.h1 
                key={currentStep}
                initial={{ opacity: 0, y: -20 }}
                animate={{ opacity: 1, y: 0 }}
                className="text-3xl md:text-4xl font-bold text-white text-center mb-4"
              >
                {getStepTitle()}
              </motion.h1>
              <div className="flex items-center justify-center gap-2">
                <span className="px-6 py-2 bg-[#4CAF50] rounded-full text-white text-sm font-medium shadow-lg">
                  Step {currentStep} of {totalSteps}
                </span>
              </div>
            </div>
            <div className="absolute bottom-0 left-0 w-full h-1 bg-gradient-to-r from-[#4CAF50] to-[#45a049]" />
          </div>

          {/* Form Content Section */}
          <div className="p-8 md:p-10 space-y-8 bg-white">
            <motion.div
              key={currentStep}
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: -20 }}
              className="space-y-6"
            >
              {currentStep === 4 ? renderProgramStep() : renderStepContent()}
            </motion.div>

            {/* Navigation Buttons */}
            <div className="flex justify-between pt-6">
              {currentStep > 1 && (
                <motion.button 
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  onClick={() => setCurrentStep(currentStep - 1)}
                  className="px-8 py-4 bg-gray-50 hover:bg-gray-100 
                           text-[#1B3168] font-semibold rounded-2xl
                           transition-all duration-300 shadow-sm
                           hover:shadow-md border border-gray-200"
                >
                  Previous
                </motion.button>
              )}
              <motion.button 
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                onClick={handleContinue}
                className={`px-10 py-4 bg-[#4CAF50] hover:bg-[#45a049] 
                         text-white font-semibold rounded-2xl
                         transition-all duration-300 shadow-lg
                         hover:shadow-xl
                         ${currentStep === 1 ? 'ml-auto' : ''}`}
              >
                {currentStep === totalSteps ? 'Submit' : 'Continue'}
              </motion.button>
            </div>
          </div>

          {/* Footer Section */}
          <div className="bg-gradient-to-r from-[#4CAF50] to-[#45a049] p-6 text-white text-center">
            <p className="font-medium text-white/90">Contact Us</p>
            <p className="text-sm text-white/80 mt-1">info@hudumacenter.com | 206-460-9022</p>
            <p className="text-sm text-white/80">30821 Pacific Hwy S, Federal Way, WA 98003</p>
          </div>
        </motion.div>
      </div>
    </div>
  );
}

export default App;
